import React from 'react';
import axios from 'axios'

class App extends React.Component{

  // componentDidMount = () =>{
  //   this.getBlogPost()
  // }
  getBlogPost = () =>{
    axios.get('/api') 
    .the((response) =>{
      const data = response.data;
      this.setState({posts: data})
      console.log("Data has been recieved");
    })
    .catch(() =>{
      alert('Error retriving data');
    });
  }

  render(){
    return(
      <h1>data</h1>
    )
  }

}
export default App
